    private void btnGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnGuardarActionPerformed

        int sllas = Integer.parseInt(comboSillas.getItemAt(comboSillas.getSelectedIndex()));
        int turbinas = Integer.parseInt(txtTurbinas.getText());
        String combustible = txtCombustible.getText();
        String tipovuelo =txtTipo.getText();
        String estadovuelo =txtEstado.getText();
        double Peso = Double.parseDouble(txtPeso.getText());
        double tama単o = Double.parseDouble(txtTama単o.getText());

        Aviones aviones = new Aviones();
        aviones.setCombustible(combustible);
        aviones.setEstadoVuelo(estadovuelo);
        aviones.setPeso(Peso);
        aviones.setSillas(sllas);
        aviones.setTama単o(tama単o);
        aviones.setTipoVuelo(tipovuelo);
        aviones.setTurbinas(turbinas);

        JOptionPane.showMessageDialog(this, "Su avion es:\n"
            + "Combustible: " + aviones.getCombustible()+ "\n"
            + "Estado de Vuelo: " + aviones.getEstadoVuelo()+ "\n"
            + "Tipo de Vuelo: " + aviones.getTipoVuelo()+ "\n"
            + "Peso: " + aviones.getPeso()+ "\n"
            + "Sillas: " + aviones.getSillas()+ "\n"
            + "Turbinas: " + aviones.getTurbinas()+ "\n"
            + "Tama単o: " + aviones.getTama単o()+ "\n");
    }//GEN-LAST:event_btnGuardarActionPerformed

